shortcuts.codeOnSuccess:
const profile = JSON.parse(response.body)
const type = profile.type

if (type == 'Text') {
    if (profile.hasData && profile.dataName && profile.dataName.length > 0) {
        const downloadUrl = getDataUrl(profile)
        const user = getVariable(/*[variable]*/"719f997c-6bf6-458f-a148-3c2a8ac5e405"/*[/variable]*/)
        const pass = getVariable(/*[variable]*/"9421719b-086f-4799-8432-158b84b6e05a"/*[/variable]*/)
        const authHeader = 'basic ' + base64encode(user + ':' + pass);

        const result = sendHttpRequest(
            downloadUrl,
            {
                method: "GET",
                headers: {
                    'Authorization': authHeader,
                },
            },
        )

        copyToClipboard(result.response.body);
        if (result.response.body.length > 100) {
            showToast('已拷贝\n' + result.response.body.substring(0, 100) + '...');
        } else {
            showToast('已拷贝\n' + result.response.body);
        }
    } else {
        const ClibboardText = profile.text;
        copyToClipboard(ClibboardText);
        showToast('已拷贝\n' + ClibboardText);

        const httpstr = httpString(ClibboardText);

        if (httpstr) {
            if (confirm('包含网址，是否打开')) {
                openUrl(httpstr[0]);
            }
        }
    }
} else if (profile.hasData && profile.dataName && profile.dataName.length > 0) {
    const downloadUrl = getVariable(/*[variable]*/"136751b5-5f38-49fc-81dc-80a825804203"/*[/variable]*/) + '/file/' + encodeURIComponent(profile.dataName)
    const inputPara = { 'downloadUrl': downloadUrl }
    showToast('文件名已拷贝，正在下载\n' + profile.dataName)
    copyToClipboard(profile.dataName)
    setVariable(/*[variable]*/"d12cab01-6d2b-44aa-a1fb-dfddc6fe7895"/*[/variable]*/, profile.dataName);
    if (type == 'Image' || isImageFile(profile.dataName)) {
        enqueueShortcut(/*[shortcut]*/"1e693964-ab59-4e9c-902b-6b94b90ff2f0"/*[/shortcut]*/, inputPara)
    } else {
        enqueueShortcut(/*[shortcut]*/"1e693964-ab59-4e9c-902b-6b94b90ff2f0"/*[/shortcut]*/, inputPara)
    }
}

function isImageFile(file) {
    const filename = file.toLowerCase();
    const list = [
        '.png',
        '.jpg',
        '.jpeg',
        '.gif',
        '.bmp',
        '.webp',
    ]
    let result = false
    list.forEach(element => {
        if (filename.endsWith(element)) {
            result = true
        }
    })
    return result
}

function httpString(s) {
    var reg = /(https?|http|ftp|file):\/\/[-A-Za-z0-9+&@#/%?=~_|!:,.;]+[-A-Za-z0-9+&@#/%=~_|]/g;
    s = s.match(reg);
    return (s)
}

function getDataUrl(profile) {
    return getVariable(/*[variable]*/"136751b5-5f38-49fc-81dc-80a825804203"/*[/variable]*/) + '/file/' + encodeURIComponent(profile.dataName)
}
